    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        btnIngresar.setEnabled(false);
        try {
            String f = "";
            f = txtNombre.getText(); 
            if (txtNombre.getText().length() > 4) {
                f = txtNombre.getText().substring(0, 5);
            }
            x=0;
            int z = y.ingresar(new Producto("1", txtNombre.getText().toUpperCase(), 1, 1));
            a = z;

            txtNombre.setText("");
            btnMostrar1.setEnabled(true);
            btnMostrar.setEnabled(true);
            String x=y.InordenNR(y.getRaiz());
            if (i == 0) {
                jPanel1.getGraphics().drawRect(z, 1, 40, 14);
                jPanel1.getGraphics().drawString(f.toUpperCase(), z + 1, 12);
                a = z;
                c = jPanel1.getWidth() / 2;
                i = 1;
            } else {
                if (y.getVerificador() == 1) {
                    b = y.getalto();
                    jPanel1.getGraphics().drawLine(a - 20, b - 26, a + 20, b);
                    jPanel1.getGraphics().drawString(f.toUpperCase(), a + 1, b + 12);
                    jPanel1.getGraphics().drawRect(a, b, 40, 14);
                } else {
                    if (y.getVerificador() == 2) {
                        d = y.getalto();
                        jPanel1.getGraphics().drawLine(a + 60, d - 26, a + 20, d);
                        jPanel1.getGraphics().drawString(f.toUpperCase(), a + 1, d + 12);
                        jPanel1.getGraphics().drawRect(a, d, 40, 14);
                    }
                }
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "EERRROR de datos");
            txtNombre.setText("");
            System.exit(1);
        }

    }//GEN-LAST:event_btnIngresarActionPerformed

